import React from 'react';
import { Link, useParams } from "react-router-dom";
//import { QuillDeltaToHtmlConverter } from "quill-delta-to-html";


const PostDetails = (props) => {

    const  {id} = useParams();

    const foundPost = props.showPostDetails.find((post) => id == post.id);
    console.log(props.show)
    return (
      <section className="post-section">
        {foundPost ? (
          <div className="card-body">
            <h1 className="card-title">{foundPost.title}</h1>
            <p
              dangerouslySetInnerHTML={{ __html: foundPost.content }}
              className="card-text"
            ></p>
            <Link className="p-5" to={`/editPost/${foundPost.id}`}>
              <button className="btn btn-primary mt-5 postButton">Edit</button>
            </Link>
          </div>
        ) : null}
      </section>
    );
};

export default PostDetails;
