import { React, useState, useRef } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import { Link } from "react-router-dom";
import {
  Container,
  Button,
  DropdownButton,
  Dropdown,
  Modal,
  Form,
} from "react-bootstrap";
import axios from "axios";

const Login = () => {
  const [signUp, setsignUp] = useState(false);
  const handleCloseSignUp = () => setsignUp(false);
  const handlesignUp = () => setsignUp(true);
  const [signIn, setsignIn] = useState(false);
  const handleCloseSignIn = () => setsignIn(false);
  const handlesignIn = () => setsignIn(true);

  const emailRef = useRef();
  const passwordRef = useRef();

  // USER SIGN IN 
  const loginUser = async () => {
    console.log(emailRef.current.value);
    try {
      axios
        .post("http://localhost:3001/users/login", {
          email: emailRef.current.value,
          password: passwordRef.current.value,
        })
        .then((resp) => {
          console.log(resp.data);
          localStorage.setItem("token", resp.data.token);
          localStorage.setItem("user", JSON.stringify(resp.data.user));
          window.location.replace("/");
        });
    } catch (error) {
      console.log(error);
    }
  };



  return (
    <Container className="login-container">
      <h3 className="text-center pacifico-font">Welcome Back!</h3>
      <Form>
        <Form.Group controlId="formBasicEmail">
          <Form.Label className="m-3">Email address</Form.Label>
          <Form.Control
            type="email"
            placeholder="Enter your email"
            ref={emailRef}
          />
        </Form.Group>

        <Form.Group controlId="formBasicPassword">
          <Form.Label className="m-3">Password</Form.Label>
          <Form.Control
            type="password"
            placeholder="Enter your Password"
            ref={passwordRef}
          />
        </Form.Group>
        <div className="text-center">
          <button
            type="button"
            className="pacifico-font m-3"
            onClick={loginUser}
          >
            Login
          </button>
        </div>
      </Form>
      <h4 className="text-center pacifico-font m-5">Don't have an account?</h4>
      <div className="text-center">
        <Link to={"/registerUser"}>
          <button className="pacifico-font">Register</button>
        </Link>
      </div>
    </Container>
  );
};

export default Login;
