import React from "react";
import { QuillDeltaToHtmlConverter } from "quill-delta-to-html";
import { Link } from "react-router-dom";
import { Container, Form } from "react-bootstrap";

const axios = require("axios").default;



const ShowPosts = (props) => {
  let contentHTML = null;

  const deletePost = async (id) => {
    try {
      axios
        .delete(`http://localhost:3001/blog/${id}`, {
          data: { id: id },
        })
        .then((resp) => {
          props.sendGetRequest();
        });
    } catch (error) {
      console.log(error);
    }
  };

  const updatePost = async (id) => {
    const foundPost = props.posts.find((post) => post.id === id);
    console.log(foundPost);
    try {
      axios
        .put(`http://localhost:3001/blog/${id}`, {
          clicked: true,
        })
        .then((resp) => props.sendGetRequest());
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <Container>
      <div className="post-container pacifico-font pt-3">
        <Form.Control
          size="lg"
          type="text"
          placeholder="Enter your title"
          className="title-input mx-auto mt-5"
        />
        <div className="btn-container mx-auto">
          {props.show.map((post, index) => (
            <Link to={`/postDetails/${post._id}`}>
              <p key={index}>{post.title}</p>
            </Link>
          ))}
          <button className="post-btn" onClick={deletePost}>
            Remove
          </button>
          <button className="post-btn" onClick={updatePost}>
            Update
          </button>
        </div>
      </div>
    </Container>
  );
};


export default ShowPosts;
