import React, { useEffect, useState } from "react";
import ReactDOM from 'react-dom';
import { BrowserRouter as Router, Switch, Route,Redirect } from 'react-router-dom';
import "bootstrap/dist/css/bootstrap.min.css";
import "../node_modules/react-quill/dist/quill.snow.css";
import * as mdb from 'mdb-ui-kit';
import { Input } from 'mdb-ui-kit';
import "@fortawesome/fontawesome-free/css/all.min.css";
import "bootstrap-css-only/css/bootstrap.min.css";
import "mdbreact/dist/css/mdb.css";

import Header from './components/header';
import AddPost from './components/addPost';
import PostDetails from './components/postDetails';
import ShowPosts from './components/showPosts';
import EditPost from './components/editPost';
import SignUp from './components/signUp';
import SignIn from './components/signIn';

const axios = require('axios').default;

const App = () => {

    const [post,setPost] = useState([]);
    console.log(post);
    const [userData, setUserData] = useState({
      token: unde
    })
    useEffect(()=>{
        sendGetRequest()
    }, []);
    const sendGetRequest = async () => {
        try { 
           axios
            .get("http://localhost:3001/post")
            .then((response) => setPost(response.data))
        } catch (err) {
            console.error(err);
        }
    };

return (
  <Router>
    <Header />
    <Switch>
      <Route exact path="/">
        <ShowPosts show={post} />
      </Route>
      <Route path="/addPost">
        <AddPost sendGetRequest={sendGetRequest} />
      </Route>
      <Route
        path="/postDetails/:id"
        render={(props) => {
          let onepost = post.find((post) => post._id === props.match.params.id);
          if (onepost) {
            return (
              <PostDetails
                showPostDetails={onepost}
                sendGetRequest={sendGetRequest}
              />
            );
          } else {
            return <Redirect to="/" />;
          }
        }}
      ></Route>
      <Route path="/editPost/:id">
        {post && <EditPost edit={post} sendGetRequest={sendGetRequest} />}
      </Route>
      <Route path="/signIn">
        <SignIn sendGetRequest={sendGetRequest} />
      </Route>
      <Route path="/signUp">
        <SignUp sendGetRequest={sendGetRequest} />
      </Route>
    </Switch>
    <p>Thanks for visiting, goodbye {localStorage.getItem("user")}</p>
  </Router>
);
};

ReactDOM.render(<App/>, document.getElementById("root"))
