import React, { useState } from "react";
import { Link } from "react-router-dom";
import { Card, Col, Container, Row, Modal, Image,  } from "react-bootstrap";


const ShowPosts = (props) => {
  const [post, setPost] = useState({
    show: false,
    moviePreview: "",
    title: "",
    type: "",
  });
  const handleClose = () =>
    setPost({
      show: false,
      postPreview: "",
      title: "",
      type: "",
      year: "",
    });
  const postShow = (poster, title, type) => {
    setPost({
      show: true,
      postPreview: poster,
      title: title,
      type: type
    });
  };
  const addBtnFav = (elm) => {
    props.addToFavorite(elm);
  };
  switch (props.data.status) {
    case "START":
      return <h1>LOADING...</h1>;
    case "FAILED":
      return <h1>FAILED</h1>;
    case "SUCCESS":
      return (
        <Container>
          <Row>
            {props.data.data.map((elm, idx) => (
              <Col
                key={idx}
                className="mt-3 mb-3"
                lg="3"
                md="4"
                sm="12"
                xs="12"
              >
                <Card className="m-auto" style={{ width: "14rem" }}>
                  <Card.Img
                    className="objectfit"
                    variant="top"
                    src={elm.Poster}
                    height="300"
                    onClick={() =>
                      postShow(elm.Poster, elm.Title, elm.Type)
                    }
                  />
                  <Card.Body className="cardBodyColor">
                    <Card.Title
                      style={{ height: "2.2rem" }}
                      className="text-center"
                    >
                      <p style={{ fontSize: "14px" }}>{elm.Title}</p>
                    </Card.Title>
                    {!props.favList.some((e) => e.imdbID === elm.imdbID) ? (
                      <BsIcons.BsFillStarFill
                        style={{ cursor: "pointer" }}
                        size={25}
                        onClick={() => {
                          addBtnFav(elm);
                        }}
                      />
                    ) : (
                      <BsIcons.BsBookmarkCheck size={25} />
                    )}
                  </Card.Body>
                </Card>
              </Col>
            ))}
          </Row>
          <Modal show={post.show} onHide={handleClose}>
            <Modal.Header style={{ backgroundColor: "#030617" }}>
              <Image className="modalImg" fluid src={post.postPreview} />
            </Modal.Header>
            <Modal.Body className="cardBodyColor">
              <Modal.Title>{post.title}</Modal.Title>
              <p>{`Type: ${post.type}`}</p>
            </Modal.Body>
            <Modal.Footer className="cardBodyColor">
              <Button
                className="cardBodyColor m-auto"
                variant="secondary"
                onClick={handleClose}
              >
                Close
              </Button>
            </Modal.Footer>
          </Modal>
        </Container>
      );
    default:
      return null;
  }
};
const mapStateToProps = (state) => {
  return {
    data: state.movieResults,
    favList: state.favoriteResults,
  };
};




















