import React from 'react';
import { Link, useParams } from "react-router-dom";
import { QuillDeltaToHtmlConverter } from "quill-delta-to-html";

const axios  = require("axios").default;

const PostDetails = (props) => {

    const  {id} = useParams();

    const foundPost = props.showPostDetails.find((post) => id == post.id);

    const updateBlog = async (id) => {
        try {
            axios
            .post("http://localhost:3001/posts/update", {id: id})
            .then((res) => props.sendGetRequest())
        }catch (error) {
            console.log(error)
        }
    }

    return (
      <section className="post-section">
        {foundPost ? (
          <div className="container blogs-container">
            <div className="row">
              <div className="card text-center">
                <div className="card-header">
                  <ul className="nav nav-tabs card-header-tabs">
                    <li className="nav-item">
                      <Link className="nav-link" to="/">
                        Home
                      </Link>
                    </li>
                    <li className="nav-item">
                      <Link className="nav-link" to="/ShowPosts">
                        Blogs
                      </Link>
                    </li>
                    <li className="nav-item">
                      <Link
                        className="nav-link"
                        to="/addPosts"
                        tabindex="-1"
                        aria-disabled="true"
                      >
                        Write Blog
                      </Link>
                    </li>
                  </ul>
                </div>
                <div className="card-body">
                  <h1 className="card-title">{foundPost.title}</h1>
                  <p
                    dangerouslySetInnerHTML={{ __html: foundPost.content }}
                    className="card-text"
                  ></p>
                  <button
                    onClick={() => {
                      updateBlogs(foundPost.id);
                    }}
                  >
                    Edit
                  </button>
                </div>
              </div>
            </div>
          </div>
        ) : null}
      </section>
    );
};

export default PostDetails;
